import React, {Suspense} from 'react'
import { Table } from 'react-bootstrap';

const VerticalTable = ({ attributes, data, option }) => {

    const OptComponent = React.lazy(() => import('../options/'+option+'Options.jsx'));

    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Attribute</th>
                    <th>Value</th>
                </tr>
            </thead>
            <tbody>
                {attributes.map( function(ele,j) {
                    return (<tr key={j}>{
                         ele.map( function(index, k) {
                            if (index==='options') {
                                ele[index] = <Suspense fallback={<div>Loading...</div>}><OptComponent id={ele._id} /></Suspense>
                            }
                            return(<td key={k}>{ele[index]}</td>)
                        } )
                    }</tr>)
                } )}
            </tbody>
      </Table>
    )
}

export default VerticalTable
