import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from '../../components/Navbar'
import VerticalTable from '../../components/ViewSingleObjectTable'
import "../../styles/forms.css"
import { useParams } from "react-router-dom";

const ViewGift = ({params}) => {

    const {id} = useParams();
    const attributes = ['_id','name','website','createdAt','updatedAt'];
	const [loading, setLoading] = useState(false);
	const [data, setData] = useState([]);

	useEffect(() => {
		console.log('use effect')
		const loadData = async () => {
			// Till the data is fetch using API
			// the Loading page will show.
			setLoading(true);

			// Await make wait until that
			// promise settles and return its result
			const response = await axios.get(`http://localhost:3000/gift/get/${id}`);
			console.log('Completed')
			// After fetching data stored it in posts state.
			setData(response.data);

			// Closed the loading page
			setLoading(false);
		};

		// Call the function
		loadData();
	}, []);

    return (
        <div className="newFormContainer">
            <Navbar />
			{loading ? <h1>Loading...</h1> : <VerticalTable attributes={attributes} data={data} option="gift" /> }
        </div>
    )
}

export default ViewGift
