import React, {useContext} from 'react'
import useFetch from '../useFetch'
import { AuthContext } from '../authContext'
import "../styles/tables.css"
import {Button} from "react-bootstrap";
import axios from 'axios';
import { faTrash, faEdit, faEye } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Options = ({ props }) => {

    const { user } = useContext(AuthContext)
    const trashClick = async () => {
        try {
            await axios.delete(
                `http://localhost:7700/customers/${props._id}`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    const editClick = async () => {
        try {
            await axios.put(
                `http://localhost:7700/customers/${props._id}`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    const viewClick  = async () => {
        try {
            await axios.put(
                `http://localhost:7700/api/customers/${props._id}`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    return (
        <div>
            <FontAwesomeIcon className="tableOptions" icon={faEye} onClick={viewClick} />
            <FontAwesomeIcon className="tableOptions" icon={faEdit} onClick={editClick} />
            <FontAwesomeIcon className="tableOptions" icon={faTrash} onClick={trashClick} />
        </div>
    )
}

export default Options;
