import React, {Suspense} from 'react'
import { Table } from 'react-bootstrap';

var Calendar = ({ month, year }) => {
    var result = [];
    var lastDay = new Date(year, month, 0).getDate();
    console.log(month);
    var w = new Date(year, month, 1).getDay();
    console.log(month);
    console.log((new Date(year, month, 1)).toISOString().split('T')[0]);
    var j=0;
    for (var i=1; i<=lastDay; i++) {
        var w = new Date(year, month, i).getDay();
        //console.log(lastDay);
        //console.log((new Date(year, month, i)).toISOString().split('T')[0]);
        if (w===6) j++;
    }



    return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>S</th>
                        <th>M</th>
                        <th>T</th>
                        <th>W</th>
                        <th>T</th>
                        <th>F</th>
                        <th>S</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                    </tr>
                </tbody>
          </Table>
        )
}

export default Calendar
