import React, { useContext, useState } from "react";
import useFetch from '../../useFetch'
import Navbar from '../../components/Navbar'
import VerticalTable from '../../components/ViewSingleObjectTable'
import axios from "axios";
import { AuthContext } from "../../authContext";
import "../../styles/forms.css"
import { useNavigate, useLocation } from "react-router-dom";

const ViewGift = ({params}) => {

    const location = useLocation()
    const giftId = location.state.id
    const [info, setInfo] = useState({});
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();
    const giftInfo  = useFetch(`gift/get/${giftId}`).data[0];
    const attributes = ['name','website','threshold','options'];

console.log(giftInfo)
    const handleChange = (e) => {
        setInfo(
            (prev) => ({
                ...prev, [e.target.id]: e.target.value
            }
            ));
    };

    const handleClick = async (e) => {
        e.preventDefault();

        const newpost = {
            ...info,
            user: user._id,
        }

        try {
            await axios.post(
                "http://localhost:7700/api/gift/create",
                newpost)
            navigate('/gifts')
        } catch (err) {
            console.log(err);
        }

    };

    return (
        <div className="newFormContainer">
            <Navbar />
            <VerticalTable />
        </div>

    )
}

export default ViewGift
