import React, {Suspense} from 'react'
import { Table } from 'react-bootstrap';

const Calendar = ({ month, year }) => {
    var result = [];
    var temp = [];
    var lastDay = new Date(year, month, 0).getDate();
    console.log('sddddddddddddddddddddddddd'+lastDay);
    var j=0;
    for (var i=1; i<=lastDay; i++) {
        var w = new Date(year, month, i).getDay();
        temp[w] = (new Date(year, month-1, i)).toLocaleDateString("en-CA", {year:"numeric", month: "2-digit", day:"2-digit"});
        if (w===6) { //end of every week, assign the week to the result, and reset the week's variables
            result[j]=temp[w];
            temp = [];
            j++;
        }
    }

    console.log(result);



    return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>S</th>
                        <th>M</th>
                        <th>T</th>
                        <th>W</th>
                        <th>T</th>
                        <th>F</th>
                        <th>S</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td></td>
                    </tr>
                </tbody>
          </Table>
        )
}

export default Calendar
