import React from "react";
import useFetch from '../../useFetch'
import Navbar from '../../components/Navbar'
import VerticalTable from '../../components/ViewSingleObjectTable'
import "../../styles/forms.css"
import { useParams } from "react-router-dom";

const ViewGift = ({params}) => {

    const {id} = useParams();
    const attributes = ['_id','name','website','createdAt','updatedAt'];
    const giftInfo  = useFetch(`http://localhost:3000/gift/get/${id}`);

    return (
        <div className="newFormContainer">
            <Navbar />
            <VerticalTable attributes={attributes} data={giftInfo} option="gift" />
        </div>
    )
}

export default ViewGift
