import React, {Suspense} from 'react'
import { Table } from 'react-bootstrap';

const Calendar = ({ month, year }) => {

    var table = {}
    var result = [];
    var lastDay = new Date(year, month + 1, 0).getDate();
    var j=0;
    for (var i=1; i<=lastDay; i++) {
        var w = new Date(year, month, i).getDay();
        result[j][w]=new Date(year, month, i);
        if (w<=6) j++;
    }

    if (table.tbody.length > 0) {
        return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        {table.thead.map( function(ele, i) {
                            return (<th key={i}>{ele}</th>)
                        } )}
                    </tr>
                </thead>
                <tbody>
                    {table.tbody.map( function(ele,j) {
                        return (<tr key={j}>{
                            table.thead.map( function(index, k) {
                                return(<td key={k}>{ele[index]}</td>)
                            } )
                        }</tr>)
                    } )}
                </tbody>
          </Table>
        )
    }
    else {
        return (<h1 className="feedback-header">Insert Feedback Message here</h1>)
    }
}

export default Calendar
