import React, {useState, useEffect} from 'react'
import Navbar from '../components/Navbar'
import Calendar from '../components/Calendar'
import "../styles/tables.css"

const Homepage = ({ type }) => {
    const [month, setMonth] = useState([]);
    const [year, setYear] = useState([]);
    const monthOptions = {
        '01':"January",
        '02':"Febuary",
        '03':"March",
        '04':"April",
        '05':"May",
        '06':"June",
        '07':"July",
        '08':"August",
        '09':"September",
        '10':"October",
        '11':"November",
        '12':"December",
    };

    const dateChange = (e) => {
        if (e.target.id === "month") setMonth(e.target.value);
        else setYear(e.target.value);
    }

    return (
        <div>
            <Navbar />
            <div className="table-container">
                <div className="title-and-options">
                    <h1>Calendar</h1>
                    <select id="month">
                        {monthOptions.map((monthName,monthVal) => {
                            return(<option value={monthVal}>{monthName}</option>)
                        })} 
                    </select>
                    <input
                        type="text"
                        id="year"
                        placeholder="Enter year"
                    />
                    <button onClick={dateChange}></button>
                    <Calendar month={month} year={year} />
                </div>

            </div>
        </div>
    )
}

export default Homepage
