import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from '../../components/Navbar'
import VerticalTable from '../../components/ViewSingleObjectTable'
import "../../styles/forms.css"
import { useParams } from "react-router-dom";

const ViewGift = ({params}) => {

    const {id} = useParams();
    const attributes = ['_id','name','website','createdAt','updatedAt'];
	const [loading, setLoading] = useState(false);
	const [posts, setPosts] = useState([]);

	useEffect(() => {
		const loadPost = async () => {
			// Till the data is fetch using API
			// the Loading page will show.
			setLoading(true);

			// Await make wait until that
			// promise settles and return its result
			const response = await axios.get(`http://localhost:3000/gift/get/${id}`);

			// After fetching data stored it in posts state.
			setPosts(response.data);

			// Closed the loading page
			setLoading(false);
		};

		// Call the function
		loadPost();
	}, []);

    return (
        <div className="newFormContainer">
            <Navbar />
            <VerticalTable attributes={attributes} option="gift" />
			{loading ? (
					<h4>Loading...</h4>
				) : (
					posts.map((item) => (
						// Presently we only fetch
						// title from the API
						<h4>{item.title}</h4>
					))
				)}
        </div>
    )
}

export default ViewGift
