import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from '../../components/Navbar'
import VerticalTable from '../../components/ViewSingleObjectTable'
import "../../styles/forms.css"
import { useParams } from "react-router-dom";

const ViewGift = ({params}) => {

    const {id} = useParams();
    const attributes = ['_id','name','website','createdAt','updatedAt'];
	const [loading, setLoading] = useState(false);
	const [data, setData] = useState([]);

	useEffect(() => {
		const loadData = async () => {
			// Till the data is fetch using API
			// the Loading page will show.
			setLoading(true);

			// Await make wait until that
			// promise settles and return its result
			axios.get(`http://localhost:3000/gift/get/${id}`).then((response) => {
		      	setData(response.data);
		    	setLoading(false);
		    }).catch((err) => {
				setLoading(false);		//error state
		 	});
			console.log('Completed');
		};

		// Call the function
		loadData();
	}, []);

    return (
        <div className="newFormContainer">
            <Navbar />
			{	loading ? data.length>0 ? <VerticalTable attributes={attributes} data={data} option="gift" /> : <h1>Loading...</h1> : <h1>Cannot Find Item</h1>
			}
        </div>
    )
}

export default ViewGift
