import React, {useState, useEffect} from 'react'
import Navbar from '../components/Navbar'
import Calendar from '../components/Calendar'
import "../styles/tables.css"

const Homepage = ({ type }) => {
    const [month, setMonth] = useState(new Date().getMonth()+1);
    const [year, setYear] = useState(new Date().getFullYear());

    const defaultMonth = new Date().getMonth()+1;
    const defaultYear = new Date().getFullYear();

    const monthChange = (e) => {
        setMonth(e.target.selectedOptions[0].value);
    }
    const dateChange = (e) => {
        console.log(e)
        if (e.target.id === "month")
            setMonth(document.getElementById('month').value);
        else
            setYear(document.getElementById('year').value);
    }
    console.log(month);

    return (
        <div>
            <Navbar />
            <div className="table-container">
                <div className="title-and-options">
                    <h1>Calendar</h1>
                    <select id="month" onChange={monthChange}>
                        <option value="01">January</option>
                        <option value="02">Febuary</option>
                        <option value="03">March</option>
                        <option value="04">April</option>
                        <option value="05">May</option>
                        <option value="06">June</option>
                        <option value="07">July</option>
                        <option value="08">August</option>
                        <option value="09">September</option>
                        <option value="10">October</option>
                        <option value="11">November</option>
                        <option value="12">December</option>
                    </select>
                    <input

                        type="number"
                        id="year"
                        placeholder="Enter year"
                    />
                    <button className="button"
                        onClick={dateChange} type="submit">
                        Select Month
                    </button>
                    <Calendar month={(Number.isInteger(month) && month>defaultYear)? month :defaultMonth} year={year} />
                </div>

            </div>
        </div>
    )
}

export default Homepage
