import React, { useState, useEffect } from "react";
import Navbar from '../../components/Navbar'
import axios from "axios";
import "../../styles/forms.css"
import { useNavigate, useParams } from "react-router-dom";

const EditGift = ( {params} ) => {

    const {id} = useParams();
    const [info, setInfo] = useState({});
    const navigate = useNavigate();
    const [responseRecieved, setResponseStatus] = useState(false);
	const [data, setData] = useState([]);

    useEffect(() => {
		const loadData = async () => {
			// Till the data is fetch using API
			// the Loading page will show.
			setResponseStatus(false);

			// Await make wait until that
			// promise settles and return its result
			axios.get(`http://localhost:3000/gift/get/${id}`).then((response) => {
		      	setData(response.data);
		    	setResponseStatus(true);
		    }).catch((err) => {
				setResponseStatus(true);		//error state
		 	});
			console.log('Completed');
		};

		// Call the function
		loadData();
	}, []);

    const handleChange = (e) => {
        setInfo(
            (prev) => ({
                ...prev, [e.target.id]: e.target.value
            }
            ));
    };

    const handleClick = async (e) => {
        e.preventDefault();

        const newpost = {
            ...info
        }

        try {
            await axios.post(
                "http://localhost:7700/api/gift/create",
                newpost)
            navigate('/gifts')
        } catch (err) {
            console.log(err);
        }

    };

    if (responseRecieved) {
        if (data.length>0) {
            return (
                <div className="newFormContainer">
                    <Navbar />
                    <div className="cpContainer">
                        <div className="formContainer">
                            <h1>Edit A Gift</h1>
                            <div className="inputContainer">
                                <div className="input">
                                    <label htmlFor="name">Name</label>
                                    <input
                                        onChange={handleChange}
                                        type="text"
                                        id="name"
                                        placeholder="Enter Name"
                                    />
                                </div>

                                <div className="input">
                                    <label htmlFor="website">Website</label>
                                    <input
                                        onChange={handleChange}
                                        type="text"
                                        id="website"
                                        placeholder="Enter website"
                                    />
                                </div>

                                <div className="input">
                                    <label htmlFor="location">Threshold</label>
                                    <input
                                        onChange={handleChange}
                                        type="number"
                                        min="0"
                                        step="0.01"
                                        id="threshold"
                                        placeholder="Enter threshold"
                                    />
                                </div>

                                <button className="button"
                                    onClick={handleClick} type="submit">
                                    Save New Entry
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }
        else {
            return (<h1 className="feedback-header">Cannot Find Item</h1>)
        }
    }
    else {
        return (<h1 className="feedback-header">Loading</h1>)
    }
}

export default EditGift
