import React from 'react'
import "../styles/tables.css"
import axios from 'axios';
import { faTrash, faEdit, faEye } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useNavigate } from "react-router-dom";

const Options = ({ id }) => {

    const navigate = useNavigate();
    const trashClick = async () => {
        try {
            await axios.delete(
                `http://localhost:7700/api/gift/delete/${id}`,
                { withCredentials: false })
            navigate('/gifts');
        }
        catch (err) {
            console.log(err)
        }
    }

    const editClick = async () => {
        try {
            await axios.put(
                `http://localhost:7700/customers/${id}`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    const viewClick  = async () => {
        try {
            await axios.get(
                `http://localhost:7700/api/gift/${id}`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    return (
        <div>
            <FontAwesomeIcon className="tableOptions" icon={faEye} onClick={viewClick} />
            <FontAwesomeIcon className="tableOptions" icon={faEdit} onClick={editClick} />
            <FontAwesomeIcon className="tableOptions" icon={faTrash} onClick={trashClick} />
        </div>
    )
}

export default Options;
