                newpost)
            navigate('/gift')
        } catch (err) {
            console.log(err);
        }

    };

    const addNewClick = async () => {
        try {
            await axios.put(
                `http://localhost:7700/api/gift/create`,
                { withCredentials: false })
            window.location.reload();
        }
        catch (err) {
            console.log(err)
        }
    }

    return (
        <div>
            <Navbar />
            <div className="table-container">
                <h1>View All Gifts</h1>
                <FontAwesomeIcon icon={faPlus} onClick={addNewClick} />
                <MainTable obj={{ data }} />
            </div>
        </div>
    )
}

export default DisplayAllGiftsTabular
