import React, { useContext, useState } from 'react'
import useFetch from '../useFetch'
import { useNavigate } from "react-router-dom";
import { AuthContext } from '../authContext'
import axios from 'axios';

import { faPlusCircle } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import MainTable from '../components/ListAllObjectsTable'
import Navbar from '../components/Navbar'
import "../styles/tables.css"
import {Button} from "react-bootstrap";

const DisplayAllGiftsTabular = ({ type }) => {

    const [info] = useState({});
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();

    // Call useFetch unconditionally
    const { data } = useFetch(`api/gift/list`)

    const addNewClick = async (e) => {
        e.preventDefault();

        const newpost = {
            ...info,
            company: user._id,
        }

        try {
            await axios.post(
                "http://localhost:7700/api/gift/create",
                newpost)
            navigate('/gift')
        } catch (err) {
            console.log(err);
        }

    };

    return (
        <div>
            <Navbar />
            <div className="table-container">
                <div className="title-and-options">
                    <h1>View All Gifts</h1>
                    <Button className="add-new" onClick={addNewClick}>
                        <FontAwesomeIcon icon={faPlusCircle} />
                    </Button>
                </div>
                <MainTable obj={{ data }} />
            </div>
        </div>
    )
}

export default DisplayAllGiftsTabular
